mapper.select:
SELECT member_name AS memberName
FROM member
WHERE member_id = #{memberId}
AND member_password = #{memberPassword}

SELECT member_id AS memberId 
	,member_level AS memberLevel
	,member_name AS memberName
FROM member
WHERE member_id = #{memberId}
AND member_password = #{memberPassword}

SELECT member_id AS memberId
FROM member
WHERE member_id = #{memberId}
